import  React ,{ useEffect, useState } from 'react';

const Scrollbar = (props) => {

    const [list, setList] = useState([]);
    const [listf, setListf] = useState([]);
    const [offset, setOffset] = useState(10);


    document.addEventListener('onPagend', function () {

        setOffset(offset + 10);

        const arr = list.slice(0, offset);
        setListf([...arr]);

    });




    useEffect(() => {

       (function (doc) {

            window.onscroll = function (event) {
                if (isEndOfElement(doc.body)) {
                    sendNewEvent('onPagend');
                }
            };

            function isEndOfElement(element) {
                //visible height + pixel scrolled = total height 
                console.log(element.clientHeight, element.scrollTop, element.scrollHeight);
                return (element.clientHeight + element.scrollTop - element.scrollHeight) == 5
            }

            function sendNewEvent(eventName) {
                var event = doc.createEvent('Event');
                event.initEvent(eventName, true, true);
                doc.dispatchEvent(event);
            }
        }(document));

       fetch('https://jsonplaceholder.typicode.com/posts')
            .then((res) => res.json())
            .then((result) => {
                const arrf = result.map((item) => {
                    return item.title;
                })
                setList([...arrf]);
                setListf([...arrf.slice(0, offset)])

            })
    }, []);



    return (
        <>
            <h1> scrollbar</h1>
            <table >
                <tbody>
                    {
                        listf.map((item, idx) => {
                            return (
                                <>
                                    <br key={Math.random()} />
                                    <br key={Math.random()} />
                                    <tr key={idx} key={idx}>
                                        <td key={Math.random()}>{idx + 1}</td>
                                        <td key={Math.random()}>{item}</td>
                                    </tr>
                                </>
                            )
                        })
                    }
                </tbody>
            </table>
        </>
    )
}

export default Scrollbar;
