import { React, useEffect, useState } from 'react';

const Scrollbar = (props) => {



    const [list, setList] = useState([]);
    const [listf, setListf] = useState([]);
    const [offset, setOffset] = useState(10)
    useEffect(() => {

        fetch('https://jsonplaceholder.typicode.com/posts')
            .then((res) => res.json())
            .then((result) => {
                const arrf = result.map((item) => {
                    return item.title;
                })
                setList([...arrf]);
                setListf([...arrf.slice(0, 10)])

            })
    }, []);

    useEffect(() => {

        const arr = list.slice(0, offset);
        setListf([...arr]);

    }, [offset]);


    function setListfinal(offset) {
        const arr = list.slice(0, offset);
        setListf([...arr]);
    }

    const getnewData = (id) => {

        if (id === offset) {
            setOffset(offset + 10);
            setListfinal(offset)
        }

    }

    return (
        <>
            <h1> scrollbar</h1>
            <table >
                <tbody>
                    {
                        listf.map((item, idx) => {
                            return (
                                <>
                                    {/* <br/> */}
                                    <tr key={idx} style={{ color:"blue" }} onMouseOver={() => getnewData(idx + 1)} key={idx}>
                                        <td>{idx + 1}</td>
                                        <td>{item}</td>
                                    </tr>
                                </>
                            )
                        })
                    }
                </tbody>
            </table>
        </>
    )
}

export default Scrollbar;
