import { React, useEffect, useState } from 'react';

const Scrollbar = (props) => {
    const [list, setList] = useState([]);
    const [listf, setListf] = useState([]);
    const [offset, setOffset] = useState(10)
    useEffect(() => {

        fetch('https://jsonplaceholder.typicode.com/posts').then((res) => {
            res.json().then((result) => {
                const arrf = result.map((item) => {
                    return item.title;
                })

                setList([...arrf])

            })
        })
    }, [])


    useEffect(() => {
        // const arr = list.slice(0, offset);
        setListf([...arr]);
    }, [offset]);

    const getnewData = (id) => {
        console.log(id);
    }

    return (
        <>
            <h1>hello this is scrollbar</h1>
            <table >
                <tbody>


                    {
                        listf.map((item, idx) => {
                            //   {   if(idx+1 < 10){
                            return (
                                <tr onMouseOver={() => getnewData(idx + 1)}>
                                    <td>{idx + 1}</td>
                                    <td>{item}</td>
                                </tr>
                                // )
                                //  }else{
                                //       return (
                                //         <tr onClick={()=>{setOffset(offset+10)}}>
                                //         <td>{idx+1}</td>
                                //         <td>{item}</td>
                                //         </tr>
                                //       );
                                //  }

                                // }
                            )
                        })
                    }
                </tbody>
            </table>
        </>
    )
}

export default Scrollbar;
