import React, { useState, useEffect } from 'react';



const Renderlist = (props) => {
    const [list, setList] = useState([]);
    const [listf, setListf] = useState([]);
    const [offset, setOffset] = useState(10);

    // document.addEventListener('scroll', () => {
    //     console.log(document.body.clientHeight, document.body.scrollTop, document.body.scrollHeight);

    // })

    window.onscroll = function(ev) {
        if ((window.innerHeight + window.scrollY) >= document.body.offsetHeight) {
            // you're at the bottom of the page
            console.log("We are at bottom");
        }
    };

    useEffect(() => {
        fetch('https://jsonplaceholder.typicode.com/posts')
            .then((res) => res.json())
            .then((result) => {
                const arrf = result.map((item) => {
                    return item.title;
                })
                setList([...arrf]);
                setListf([...arrf.slice(0, offset)])
                console.log(document.body.clientHeight, document.body.scrollTop, document.body.scrollHeight);

            })


    }, [])







    return (
        <>
            <hi>RenderList</hi>

            <table >
                <tbody>
                    {
                        listf.map((item, idx) => {
                            return (
                                <>
                                    <br key={Math.random()} />
                                    <br key={Math.random()} />
                                    <br key={Math.random()} />
                                    <br key={Math.random()} />
                                    <tr key={idx} key={idx}>
                                        <td key={Math.random()}>{idx + 1}</td>
                                        <td key={Math.random()}>{item}</td>
                                    </tr>
                                </>
                            )

                        })

                    }
                </tbody>

            </table>



        </>
    )
}

export default Renderlist;
